
(function () {
  'use strict';
  let menu = document.querySelector('.menu-header__list');
  let menuMobile = document.querySelector('.hamburger');

  let openMenu = () => {
    menuMobile.classList.add('hamburger__opened');
    menu.classList.add('menu-header__opened');
    window.addEventListener('keydown',onEscPress);
    menuMobile.addEventListener('keydown',onEnterPress)
  };

  let closeMenu = () => {
    menuMobile.classList.remove('hamburger__opened');
    menu.classList.remove('menu-header__opened');
    window.removeEventListener('keydown',onEscPress);
  };

  let onEscPress = (event) => {
    if (event.code === 'Escape'){
      closeMenu();
    }
  };

  let onEnterPress = () => {
    if (event.code === 'Enter' && menuMobile.classList.contains('hamburger__opened')){
      closeMenu();
    }else if(event.code === 'Enter'){
      openMenu();
    }
  };

  menuMobile.addEventListener('click',function () {
    if (menuMobile.classList.contains('hamburger__opened')) closeMenu();
    else openMenu();
  });
  menuMobile.addEventListener('keydown',onEnterPress)
})();
(function () {
  function moveItem() {
    let windowHeight = document.documentElement.clientHeight;
    let itemsLeft = document.body.querySelectorAll('.transitionLeft');
    let itemsRight = document.body.querySelectorAll('.transitionRight');

    function makeDo(items) {
      for(let item of items){
        let coords = item.getBoundingClientRect();
        if(coords.top < windowHeight-100 && coords.top > -windowHeight || coords.bottom > -windowHeight && coords.bottom < windowHeight){
          item.classList.remove(items.name);
          item.style.transform = '';
        }
      }
    }
    makeDo(itemsLeft);
    makeDo(itemsRight);
  }
  window.onscroll = moveItem;
  moveItem();

})();

(function() {
	let transitionLeft = document.querySelectorAll('.transitionLeft');
	for (let item of transitionLeft){
	  item.setAttribute('style','transform: translateX(-'+ document.body.clientWidth +'px)')
  }
  let transitionRight = document.querySelectorAll('.transitionRight');
  for (let item of transitionRight){
    item.setAttribute('style','transform: translateX('+ document.body.clientWidth +'px)')
  }
})();
